
exports.options = [];

exports.init = function(){

    GizmoCore.modules.fields.options = [];
    GizmoCore.modules.fields.options.push({
        type : 'text',
        name : 'Text'
    });
    GizmoCore.modules.fields.options.push({
        type : 'textarea',
        name : 'Long Text'
    });
    GizmoCore.modules.fields.options.push({
        type : 'select',
        name : 'Select'
    });
    GizmoCore.modules.fields.options.push({
        type : 'multiselect',
        name : 'MultiSelect',
        multiple : true
    });
//    GizmoCore.modules.fields.options.push({
//        type : 'file',
//        name : 'File'
//    });
    GizmoCore.modules.fields.options.push({
        type : 'radio',
        name : 'Radio'
    });
    GizmoCore.modules.fields.options.push({
        type : 'checkbox',
        name : 'Checkbox'
    });
}

exports.getOptions = function(){
    return GizmoCore.modules.fields.options;
}


